import React, { useState, useEffect } from 'react';

interface Sound {
	id: number;
	name: string;
	previews: {
		'preview-hq-mp3': string;
	};
}

const MainPage = () => {
	const [sounds, setSounds] = useState<Sound[]>([]);
	const [query, setQuery] = useState<string>('flute');
	const [selectedSound, setSelectedSound] = useState<Sound | null>(null);

	useEffect(() => {
		fetchSounds(query);
	}, [query]);

	const fetchSounds = async (searchQuery: string) => {
		try {
			const apiKey = 'Aj9x06vq60VC37YLo9psCPwzvEIyTu0eBQfphtoz';
			const queryParams = new URLSearchParams({
				query: searchQuery,
				fields: 'id,name,previews',
				token: apiKey
			});
			const response = await fetch(
				`https://freesound.org/apiv2/search/text/?${queryParams}`
			);
			const data = await response.json();
			const randomPianoSounds = data.results.slice(0, 16);
			setSounds(randomPianoSounds);
		} catch (error) {
			console.error('Error fetching sounds:', error);
		}
	};

	const handleSoundClick = (sound: Sound) => {
		setSelectedSound(sound);
	};

	const handleButtonClick = (index: number) => {
		if (selectedSound) {
			// Replace the sound at the selected index with the new sound
			const updatedSounds = [...sounds];
			updatedSounds[index] = selectedSound;
			setSounds(updatedSounds);
			setSelectedSound(null);
		}
	};

	const handleSearch = (e: React.FormEvent) => {
		e.preventDefault();
		fetchSounds(query);
	};

	return (
		<div className="main-page">
			<div className="content">
				<div className="logo">
					<h1>SynthPiser</h1>
				</div>
				<form onSubmit={handleSearch}>
					<input
						type="text"
						value={query}
						onChange={(e) => setQuery(e.target.value)}
						placeholder="Enter search query"
						className="search-bar"
					/>
				</form>
				<div className="sounds">
					{sounds.map((sound, index) => (
						<button
							key={sound.id}
							onClick={() => handleSoundClick(sound)}
							className="sound-button"
						>
							{sound.name}
						</button>
					))}
				</div>
				<div className="other-buttons">
					{Array.from({ length: 16 }).map((_, index) => (
						<button
							key={index}
							onClick={() => handleButtonClick(index)}
							className="other-button"
						>
							{sounds[index] ? sounds[index].name : ''}
						</button>
					))}
				</div>
			</div>
		</div>
	);
};

export default MainPage;
