import React, { useState, useEffect } from 'react';

const MainPage: React.FC = () => {
	const [selectedPack, setSelectedPack] = useState<string | null>(null);
	const [sounds, setSounds] = useState<string[]>([]);

	useEffect(() => {
		if (selectedPack) {
			fetchSounds(selectedPack);
		}
	}, [selectedPack]);

	const fetchSounds = async (pack: string) => {
		try {
			const response = await fetch(
				`https://freesound.org/apiv2/search/text/?query=${pack}&token=Aj9x06vq60VC37YLo9psCPwzvEIyTu0eBQfphtoz`
			);
			if (!response.ok) {
				throw new Error(`Failed to fetch sounds for pack ${pack}`);
			}
			const data = await response.json();
			setSounds(data.results.map((result: any) => result.url));
		} catch (error) {
			console.error('Error fetching sounds:', error);
		}
	};

	const handlePackClick = (pack: string) => {
		setSelectedPack(pack);
	};

	const handleSoundClick = (soundUrl: string) => {
		const audio = new Audio(soundUrl);
		audio.play().catch((error) => {
			console.error('Error playing sound:', error);
		});
	};

	const renderPacks = () => {
		const packs = ['drums', 'piano' /* Add more packs here */];
		return packs.map((pack, index) => (
			<button key={index} onClick={() => handlePackClick(pack)}>
				{pack}
			</button>
		));
	};

	const renderSounds = () => {
		return sounds.map((soundUrl, index) => (
			<button key={index} onClick={() => handleSoundClick(soundUrl)}>
				Sound {index + 1}
			</button>
		));
	};

	return (
		<div className="main-page">
			<div className="logo">
				<h1>SynthPiser</h1>
			</div>
			<div className="packs">{renderPacks()}</div>
			<div className="sounds">{renderSounds()}</div>
		</div>
	);
};

export default MainPage;
