import React, { useState, useEffect } from 'react';
import axios from 'axios';

interface Sound {
	id: number;
	name: string;
	preview_url?: string;
	audioUrl?: string;
}

interface ApiSound {
	id: number;
	name: string;
	previews: {
		url: string;
	}[];
}

const MainPage: React.FC = () => {
	const [sounds, setSounds] = useState<Sound[]>([]);
	const [isPlaying, setIsPlaying] = useState(false);
	const [currentSound, setCurrentSound] = useState<Sound | null>(null);

	useEffect(() => {
		const fetchSounds = async () => {
			const response = await axios.get(
				'https://api.freesound.org/v1/sounds/?q=sound effects&limit=16'
			);
			const data = response.data.results;
			setSounds(
				data.map((sound: ApiSound) => ({
					id: sound.id,
					name: sound.name,
					preview_url: sound.previews[0].url
				}))
			);
		};

		fetchSounds();
	}, []);

	const handleSoundClick = (sound: Sound) => {
		setCurrentSound(sound);
		setIsPlaying(true);
	};

	const handleAudioEnded = () => {
		setIsPlaying(false);
	};

	return (
		<div className="main-page">
			<div className="content">
				<div className="logo">
					<h1>SynthPiser</h1>
				</div>
				<div className="sounds">
					{sounds.map((sound) => (
						<button key={sound.id} onClick={() => handleSoundClick(sound)}>
							{sound.name}
						</button>
					))}
				</div>
			</div>
			{currentSound?.preview_url && ( // Play sound effects using preview URL
				<audio
					src={currentSound.preview_url}
					autoPlay={isPlaying}
					onEnded={handleAudioEnded}
				/>
			)}
			{currentSound?.audioUrl && ( // Play full audio (if available)
				<audio
					src={currentSound.audioUrl}
					autoPlay={isPlaying}
					onEnded={handleAudioEnded}
				/>
			)}
		</div>
	);
};

export default MainPage;
