import React, { useState, useEffect } from 'react';
import { DndProvider, useDrag, useDrop } from 'react-dnd';
import { HTML5Backend } from 'react-dnd-html5-backend';

interface Sound {
	id: number;
	name: string;
	previews: {
		'preview-hq-mp3': string;
	};
	tags: string[] | undefined;
}

const MainPage = () => {
	const [sounds, setSounds] = useState<Sound[]>([]);
	const [query, setQuery] = useState<string>('Drums');

	useEffect(() => {
		fetchSounds(query);
	}, [query]);

	const fetchSounds = async (searchQuery: string) => {
		try {
			const apiKey = 'Aj9x06vq60VC37YLo9psCPwzvEIyTu0eBQfphtoz';
			const queryParams = new URLSearchParams({
				query: searchQuery,
				fields: 'id,name,previews,tags',
				token: apiKey
			});
			const response = await fetch(
				`https://freesound.org/apiv2/search/text/?${queryParams}`
			);
			const data = await response.json();
			const randomPianoSounds = data.results.slice(0, 16);
			setSounds(randomPianoSounds);
		} catch (error) {
			console.error('Error fetching sounds:', error);
		}
	};

	const handleSoundClick = (previewUrl: string) => {
		const audio = new Audio(previewUrl);
		audio.play();
	};

	const handleSearch = (e: React.FormEvent) => {
		e.preventDefault();
		fetchSounds(query);
	};

	const handleDrop = (index: number, sound: Sound) => {
		const newSounds = [...sounds];
		newSounds[index] = sound;
		setSounds(newSounds);
	};

	return (
		<div className="main-page">
			<div className="content">
				<div className="logo">
					<h1>SynthPiser</h1>
				</div>
				<form onSubmit={handleSearch}>
					<input
						type="text"
						value={query}
						onChange={(e) => setQuery(e.target.value)}
						placeholder="Search"
						className="search-bar"
					/>
				</form>
				<div className="sounds">
					{sounds.map((sound, index) => (
						<SoundButton
							key={index}
							index={index}
							sound={sound}
							onDrop={handleDrop}
						/>
					))}
				</div>
				<div className="embedded-keyboard">
					{Array.from(Array(16).keys()).map((_, index) => (
						<button key={index} className="embedded-key">
							<div></div>
						</button>
					))}
				</div>
			</div>
		</div>
	);
};

interface SoundButtonProps {
	index: number;
	sound: Sound;
	onDrop: (index: number, sound: Sound) => void;
}

const SoundButton: React.FC<SoundButtonProps> = ({ index, sound, onDrop }) => {
	const [{ isDragging }, drag] = useDrag({
		type: 'SOUND',
		item: { index, sound },
		collect: (monitor) => ({
			isDragging: !!monitor.isDragging()
		})
	});

	const [, drop] = useDrop({
		accept: 'SOUND',
		drop: (item: { index: number; sound: Sound }) => onDrop(index, item.sound)
	});

	return (
		<div
			ref={(node) => drag(drop(node))}
			className={`sound-button ${isDragging ? 'dragging' : ''}`}
		>
			<img src="/assets/note.png" alt="" height={20} width={20} />
		</div>
	);
};

const App = () => {
	return (
		<DndProvider backend={HTML5Backend}>
			<MainPage />
		</DndProvider>
	);
};

export default App;
