import React, { useState, useEffect } from 'react';

interface Sound {
	id: number;
	name: string;
	preview_url: string;
}

const MainPage = () => {
	const [sounds, setSounds] = useState<Sound[]>([]);

	useEffect(() => {
		const fetchSounds = async () => {
			try {
				const apiKey = 'Aj9x06vq60VC37YLo9psCPwzvEIyTu0eBQfphtoz';
				const response = await fetch(
					'https://freesound.org/apiv2/search/text/?query=&fields=id,name,previews&token=' +
						apiKey
				);
				const data = await response.json();
				const randomIndices = getRandomIndices(data.results.length, 16);
				const randomSounds = randomIndices.map(
					(index: number) => data.results[index]
				);
				setSounds(randomSounds);
			} catch (error) {
				console.error('Error fetching sounds:', error);
			}
		};

		fetchSounds();
	}, []);

	const getRandomIndices = (max: number, count: number): number[] => {
		const indices = [];
		while (indices.length < count) {
			const randomIndex = Math.floor(Math.random() * max);
			if (!indices.includes(randomIndex)) {
				indices.push(randomIndex);
			}
		}
		return indices;
	};

	const handleSoundClick = async (sound: Sound) => {
		try {
			if (sound.preview_url) {
				const audio = new Audio(sound.preview_url);
				audio.play();
			} else {
				console.error('No preview URL available for the sound.');
			}
		} catch (error) {
			console.error('Error fetching sound data:', error);
		}
	};

	return (
		<div className="main-page">
			<div className="content">
				<div className="logo">
					<h1>SynthPiser</h1>
				</div>
				<div className="sounds">
					{sounds.map((sound) => (
						<button
							key={sound.id}
							onClick={() => handleSoundClick(sound)}
							className="sound-button"
						>
							{sound.name}
						</button>
					))}
				</div>
			</div>
		</div>
	);
};

export default MainPage;
