import React, { useState, useEffect } from 'react';

interface Sound {
	id: number;
	name: string;
	previews: {
		'preview-hq-mp3': string;
	};
}

const MainPage = () => {
	const [sounds, setSounds] = useState<Sound[]>([]);

	useEffect(() => {
		const fetchRandomPianoSounds = async () => {
			try {
				const apiKey = 'Aj9x06vq60VC37YLo9psCPwzvEIyTu0eBQfphtoz';
				const queryParams = new URLSearchParams({
					query: 'piano',
					fields: 'id,name,previews',
					token: apiKey
				});
				const response = await fetch(
					`https://freesound.org/apiv2/search/text/?${queryParams}`
				);
				const data = await response.json();
				const randomPianoSounds = data.results
					.sort(() => Math.random() - 0.5)
					.slice(0, 16);
				setSounds(randomPianoSounds);
			} catch (error) {
				console.error('Error fetching random piano sounds:', error);
			}
		};

		fetchRandomPianoSounds();
	}, []);

	const handleSoundClick = (previewUrl: string) => {
		const audio = new Audio(previewUrl);
		audio.play();
	};

	return (
		<div className="main-page">
			<div className="content">
				<div className="logo">
					<h1>SynthPiser</h1>
				</div>
				<div className="sounds">
					{sounds.map((sound) => (
						<button
							key={sound.id}
							onClick={() => handleSoundClick(sound.previews['preview-hq-mp3'])}
							className="sound-button"
						>
							{sound.name}
						</button>
					))}
				</div>
			</div>
		</div>
	);
};

export default MainPage;
