import React, { useState, useEffect } from 'react';
import Draggable, { DraggableEvent, DraggableData } from 'react-draggable';

interface Sound {
	id: number;
	name: string;
	previews: {
		'preview-hq-mp3': string;
	};
	tags: string[] | undefined;
}

const MainPage = () => {
	const [sounds, setSounds] = useState<Sound[]>([]);
	const [query, setQuery] = useState<string>('Drums');
	const [draggedSound, setDraggedSound] = useState<Sound | null>(null);
	const [draggedSoundPosition, setDraggedSoundPosition] = useState<{
		x: number;
		y: number;
	}>({ x: 0, y: 0 });
	const [targetButtonId, setTargetButtonId] = useState<number | null>(null);

	useEffect(() => {
		fetchSounds(query);
	}, [query]);

	const fetchSounds = async (searchQuery: string) => {
		try {
			const apiKey = 'Aj9x06vq60VC37YLo9psCPwzvEIyTu0eBQfphtoz';
			const queryParams = new URLSearchParams({
				query: searchQuery,
				fields: 'id,name,previews,tags',
				token: apiKey
			});
			const response = await fetch(
				`https://freesound.org/apiv2/search/text/?${queryParams}`
			);
			const data = await response.json();
			const randomPianoSounds = data.results.slice(0, 16);
			setSounds(randomPianoSounds);
		} catch (error) {
			console.error('Error fetching sounds:', error);
		}
	};

	const handleSoundClick = (previewUrl: string) => {
		const audio = new Audio(previewUrl);
		audio.play();
	};

	const handleSearch = (e: React.FormEvent) => {
		e.preventDefault();
		fetchSounds(query);
	};

	const handleDragStart = (sound: Sound) => {
		setDraggedSound(sound);
	};

	const handleDragStop = () => {
		if (targetButtonId !== null && draggedSound !== null) {
			// Assign the dragged sound to the target button
			// You can implement your logic here
			console.log(`Assign ${draggedSound.name} to button ${targetButtonId}`);
		}
		setDraggedSound(null);
		setTargetButtonId(null);
		setDraggedSoundPosition({ x: 0, y: 0 });
	};

	const handleTargetButtonEnter = (buttonId: number) => {
		setTargetButtonId(buttonId);
	};

	const handleDrag = (e: DraggableEvent, data: DraggableData) => {
		setDraggedSoundPosition({ x: data.x, y: data.y });
	};

	return (
		<div className="main-page">
			<div className="content">
				<div className="logo">
					<h1>SynthPiser</h1>
				</div>
				<form onSubmit={handleSearch}>
					<input
						type="text"
						value={query}
						onChange={(e) => setQuery(e.target.value)}
						placeholder="Search"
						className="search-bar"
					/>
				</form>
				<div className="sounds">
					{sounds.map((sound) => (
						<Draggable
							key={sound.id}
							onStart={() => handleDragStart(sound)}
							onStop={handleDragStop}
							onDrag={handleDrag}
						>
							<button
								className="sound-button"
								onClick={() =>
									handleSoundClick(sound.previews['preview-hq-mp3'])
								}
								style={{
									position: 'absolute',
									transform: `translate(${draggedSoundPosition.x}px, ${draggedSoundPosition.y}px)`
								}}
							>
								<img src="/assets/note.png" alt="" height={20} width={20} />
							</button>
						</Draggable>
					))}
				</div>
				<div className="embedded-keyboard">
					{[...Array(16)].map((_, index) => (
						<button
							key={index}
							className="embedded-key"
							onMouseEnter={() => handleTargetButtonEnter(index)}
						>
							<div></div>
						</button>
					))}
				</div>
			</div>
		</div>
	);
};

export default MainPage;
