import React, { useState, useEffect } from 'react';

interface Sound {
	id: number;
	name: string;
	preview_url: string;
}

const MainPage = () => {
	const [sounds, setSounds] = useState<Sound[]>([]);

	useEffect(() => {
		const fetchSounds = async () => {
			const apiKey = 'Aj9x06vq60VC37YLo9psCPwzvEIyTu0eBQfphtoz';
			const response = await fetch(
				'https://freesound.org/apiv2/search/text/?query=piano&token=' + apiKey
			);
			const data = await response.json();

			const detailedSounds = await Promise.all(
				data.results.slice(0, 16).map(async (sound: Sound) => {
					const soundResponse = await fetch(
						`https://freesound.org/apiv2/sounds/${sound.id}/`
					);
					const soundData = await soundResponse.json();
					return {
						...sound,
						...soundData
					};
				})
			);

			setSounds(detailedSounds);
		};

		fetchSounds();
	}, []);

	const handleSoundClick = (sound: Sound) => {
		const audio = new Audio(sound.preview_url);
		audio.play();
	};

	return (
		<div className="main-page">
			<div className="content">
				<div className="logo">
					<h1>SynthPiser</h1>
				</div>
				<div className="sounds">
					{sounds.map((sound) => (
						<button
							key={sound.id}
							onClick={() => handleSoundClick(sound)}
							className="sound-button"
						>
							{sound.name}
						</button>
					))}
				</div>
			</div>
		</div>
	);
};

export default MainPage;
