import React, { useState, useEffect, useRef } from 'react';
import { FaPlay, FaPause, FaStop, FaRedo } from 'react-icons/fa';

type BeatControlsProps = {
	socket: WebSocket | null;
};

const BeatControls = ({ socket }: BeatControlsProps) => {
	const [timerValue, setTimerValue] = useState<number>(0);
	const [isPlaying, setIsPlaying] = useState<boolean>(false);
	const [timerRunning, setTimerRunning] = useState<boolean>(false);
	const [recordedButtons, setRecordedButtons] = useState<number[]>([]);
	const timerRef = useRef<number | null>(null);

	const buttonPins = [3, 7, 11]; // Example button pins for demonstration

	const togglePlayPause = () => {
		setIsPlaying(!isPlaying);
		if (!timerRunning) {
			setTimerValue(0);
			setTimerRunning(true);
			timerRef.current = window.setInterval(() => {
				setTimerValue((prevValue) => {
					if (prevValue >= 10000) {
						// Assuming 10 seconds equals 10000 milliseconds
						setTimerRunning(false);
						window.clearInterval(timerRef.current!);
						return 0;
					}
					return prevValue + 100; // Increment timer by 100 milliseconds
				});
			}, 100); // Update every 100 milliseconds
		} else {
			setTimerRunning(false);
			window.clearInterval(timerRef.current!);
		}
	};

	const stopPlayback = () => {
		setIsPlaying(false);
		setTimerRunning(false);
		setTimerValue(0);
		if (timerRef.current) {
			clearInterval(timerRef.current);
		}
	};

	const resetTimer = () => {
		setRecordedButtons([]);
		setTimerValue(0);
	};

	const handleButtonPress = (pin: number) => {
		setRecordedButtons((prevButtons) => [...prevButtons, timerValue]);
	};

	const handleClearRecordedButtons = () => {
		setRecordedButtons([]);
	};

	useEffect(() => {
		if (socket) {
			socket.onmessage = (event: any) => {
				const data = JSON.parse(event.data);
				if (data.type === 'assign_sound' && data.pin) {
					setRecordedButtons((prevButtons) => [...prevButtons, timerValue]);
				}
			};
		}
	}, [socket, timerValue]);

	useEffect(() => {
		if (isPlaying) {
			let index = 0;
			const interval = setInterval(() => {
				if (index < recordedButtons.length) {
					setTimerValue(recordedButtons[index]);
					index++;
				} else {
					clearInterval(interval);
					setIsPlaying(false);
				}
			}, 100); // Update every 100 milliseconds
			return () => clearInterval(interval);
		}
	}, [isPlaying, recordedButtons]);

	return (
		<div className="beat-controls">
			<div className="timer-axis">
				<div className="timer-display">{(timerValue / 1000).toFixed(3)} s</div>
				{recordedButtons.map((time, index) => (
					<div
						key={index}
						className="button-indicator"
						style={{ left: `${(time / 10000) * 100}%` }} // Adjust the position based on time
					>
						{/* Dot indicator */}
					</div>
				))}
			</div>
			<div className="playback-controls">
				<button onClick={togglePlayPause}>
					{isPlaying ? <FaPause /> : <FaPlay />}
				</button>
				<button onClick={stopPlayback}>
					<FaStop />
				</button>
				<button onClick={resetTimer}>
					<FaRedo />
				</button>
				<button onClick={handleClearRecordedButtons}>Clear</button>
			</div>
			<div className="static-buttons">
				{buttonPins.map((pin, index) => (
					<button key={index} onClick={() => handleButtonPress(pin)}>
						Test Button {index + 1}
					</button>
				))}
			</div>
		</div>
	);
};

export default BeatControls;
