import React, { useState } from 'react';

const MainPage: React.FC = () => {
	const [selectedPack, setSelectedPack] = useState<string | null>(null);

	const packs = [
		'Drums',
		'Keys (Piano, Organ, Synth)' /* Add more packs here */
	];

	const handlePackClick = (pack: string) => {
		setSelectedPack(pack);
	};

	const handleSoundDragStart =
		(sound: string) => (event: React.DragEvent<HTMLButtonElement>) => {
			event.dataTransfer.setData('text/plain', sound);
		};

	const renderPacks = () => {
		return packs.map((pack, index) => (
			<div key={index} onClick={() => handlePackClick(pack)}>
				{pack}
			</div>
		));
	};

	const renderSounds = () => {
		if (!selectedPack) {
			return null;
		}
		// Fetch sounds for the selected pack and render them here
		return <div>{/* Render sounds for the selected pack */}</div>;
	};

	const renderButtons = () => {
		// Return JSX for 16 buttons
		return <div>{/* Render 16 buttons here */}</div>;
	};

	return (
		<div className="main-page">
			<div className="logo">
				<h1>SynthPiser</h1>
			</div>
			<div className="packs">{renderPacks()}</div>
			<div className="sounds">{renderSounds()}</div>
			<div className="buttons">{renderButtons()}</div>
		</div>
	);
};

export default MainPage;
