import React, { useState, useEffect } from 'react';
import Draggable from 'react-draggable';

interface Sound {
	id: number;
	name: string;
	previews: {
		'preview-hq-mp3': string;
	};
	tags: string[] | undefined;
}

const MainPage = () => {
	const [sounds, setSounds] = useState<Sound[]>([]);
	const [query, setQuery] = useState<string>('Drums');
	const [draggedSound, setDraggedSound] = useState<Sound | null>(null);

	useEffect(() => {
		fetchSounds(query);
	}, [query]);

	const fetchSounds = async (searchQuery: string) => {
		try {
			const apiKey = 'Aj9x06vq60VC37YLo9psCPwzvEIyTu0eBQfphtoz';
			const queryParams = new URLSearchParams({
				query: searchQuery,
				fields: 'id,name,previews,tags',
				token: apiKey
			});
			const response = await fetch(
				`https://freesound.org/apiv2/search/text/?${queryParams}`
			);
			const data = await response.json();
			const randomPianoSounds = data.results.slice(0, 16);
			setSounds(randomPianoSounds);
		} catch (error) {
			console.error('Error fetching sounds:', error);
		}
	};

	const handleSoundClick = (previewUrl: string) => {
		const audio = new Audio(previewUrl);
		audio.play();
	};

	const handleSearch = (e: React.FormEvent) => {
		e.preventDefault();
		fetchSounds(query);
	};

	const handleDragStart = (sound: Sound) => {
		setDraggedSound(sound);
	};

	const handleDragStop = () => {
		setDraggedSound(null);
	};

	const handleDrop = (index: number) => {
		if (draggedSound) {
			const newSounds = [...sounds];
			newSounds[index] = draggedSound;
			setSounds(newSounds);
		}
	};

	return (
		<div className="main-page">
			<div className="content">
				<div className="logo">
					<h1>SynthPiser</h1>
				</div>
				<form onSubmit={handleSearch}>
					<input
						type="text"
						value={query}
						onChange={(e) => setQuery(e.target.value)}
						placeholder="Search"
						className="search-bar"
					/>
				</form>
				<div className="sounds">
					{sounds.map((sound, index) => (
						<Draggable
							key={sound.id}
							onStop={() => handleDragStop()}
							onStart={() => handleDragStart(sound)}
						>
							<button
								onClick={() =>
									handleSoundClick(sound.previews['preview-hq-mp3'])
								}
								className="sound-button"
								style={{ backgroundColor: draggedSound ? '#000000' : '' }}
							>
								<img src="/assets/note.png" alt="" height={20} width={20} />
							</button>
						</Draggable>
					))}
				</div>
				<div className="embedded-keyboard">
					{Array.from(Array(16).keys()).map((_, index) => (
						<button
							key={index}
							className="embedded-key"
							onDrop={() => handleDrop(index)}
							onDragOver={(e) => e.preventDefault()}
							style={{ backgroundColor: draggedSound ? '#000000' : '' }}
						>
							<div></div>
						</button>
					))}
				</div>
			</div>
		</div>
	);
};

export default MainPage;
