import React from 'react';

const Keyboard: React.FC = () => {
	const audioContext = new AudioContext();

	const loadSample = async (note: number) => {
		const response = await fetch(`/piano-flash-cards/audio/C${note}v10.mp3`);
		const arrayBuffer = await response.arrayBuffer();
		return audioContext.decodeAudioData(arrayBuffer);
	};

	const playNote = async (note: number) => {
		const sample = await loadSample(note);
		const source = audioContext.createBufferSource();
		source.buffer = sample;
		source.connect(audioContext.destination);
		source.start(0);
	};

	const handleButtonClick = async (note: number) => {
		await playNote(note);
	};

	// Generate buttons for piano keys
	const renderPianoKeys = () => {
		const pianoKeys: JSX.Element[] = [];
		const rows = 2;
		const columns = 8;

		for (let row = 0; row < rows; row++) {
			for (let col = 0; col < columns; col++) {
				const note = row * columns + col + 1; // Note numbers start from 1
				pianoKeys.push(
					<button key={note} onClick={() => handleButtonClick(note)}>
						{`C${note}`}
					</button>
				);
			}
			pianoKeys.push(<br key={`row-${row}`} />); // Add line break after each row
		}

		return pianoKeys;
	};

	return <div className="keyboard">{renderPianoKeys()}</div>;
};

export default Keyboard;
