import React, { useState, useEffect, useRef } from 'react';
import {
	FaPlay,
	FaPause,
	FaStop,
	FaRedo,
	FaCircle,
	FaSquare
} from 'react-icons/fa';

type BeatControlsProps = {
	socket: WebSocket | null;
};

const BeatControls: React.FC<BeatControlsProps> = ({
	socket
}: BeatControlsProps) => {
	const [timerValue, setTimerValue] = useState<number>(0);
	const [isPlaying, setIsPlaying] = useState<boolean>(false);
	const [timerRunning, setTimerRunning] = useState<boolean>(false);
	const [recordedButtons, setRecordedButtons] = useState<number[]>([]);
	const timerRef = useRef<number | null>(null);

	const togglePlayPause = () => {
		setIsPlaying(!isPlaying);
		if (!timerRunning) {
			setTimerValue(0);
			setTimerRunning(true);
			timerRef.current = window.setInterval(() => {
				setTimerValue((prevValue) => {
					if (prevValue >= 10) {
						setTimerRunning(false);
						window.clearInterval(timerRef.current!);
						return 0;
					}
					return prevValue + 1;
				});
			}, 1000);
		} else {
			setTimerRunning(false);
			window.clearInterval(timerRef.current!);
		}
	};

	const stopPlayback = () => {
		setIsPlaying(false);
		setTimerRunning(false);
		setTimerValue(0);
		if (timerRef.current) {
			clearInterval(timerRef.current);
		}
	};

	const resetTimer = () => {
		setRecordedButtons([]);
		setTimerValue(0);
	};

	const handleClearRecordedButtons = () => {
		setRecordedButtons([]);
	};

	// TEST
	const mockSocket = {
		onmessage: null,
		send: (data) => {
			if (mockSocket.onmessage) {
				const pin = Math.floor(Math.random() * 16) + 1; // Random pin number
				mockSocket.onmessage({
					data: JSON.stringify({ type: 'assign_sound', pin })
				});
			}
		},
		close: () => {}
	};

	// useEffect(() => {
	// 	if (socket) {
	// 		socket.onmessage = (event) => {
	// 			const data = JSON.parse(event.data);
	// 			if (data.type === 'assign_sound' && data.pin) {
	// 				setRecordedButtons((prevButtons) => [...prevButtons, data.pin]);
	// 			}
	// 		};
	// 	}
	// }, [socket]);

	useEffect(() => {
		if (isPlaying) {
			let index = 0;
			const interval = setInterval(() => {
				if (index < recordedButtons.length) {
					setTimerValue(recordedButtons[index]);
					index++;
				} else {
					clearInterval(interval);
					setIsPlaying(false);
				}
			}, 1000);
			return () => clearInterval(interval);
		}
	}, [isPlaying, recordedButtons]);

	return (
		<div className="beat-controls">
			<div className="timer-axis">
				<div className="timer-display">{timerValue}</div>
				{recordedButtons.map((buttonPin, index) => (
					<div key={index} className="button-indicator">
						{buttonPin % 2 === 0 ? <FaSquare /> : <FaCircle />}
					</div>
				))}
			</div>
			<div className="playback-controls">
				<button onClick={togglePlayPause}>
					{isPlaying ? <FaPause /> : <FaPlay />}
				</button>
				<button onClick={stopPlayback}>
					<FaStop />
				</button>
				<button onClick={resetTimer}>
					<FaRedo />
				</button>
				<button onClick={handleClearRecordedButtons}>Clear</button>
			</div>
		</div>
	);
};

export default BeatControls;
