import React, { useState, useEffect } from 'react';

interface Sound {
	id: number;
	name: string;
	previews: {
		'preview-hq-mp3': string;
	};
}

const MainPage = () => {
	const [sounds, setSounds] = useState<Sound[]>([]);
	const [query, setQuery] = useState<string>(' ');

	useEffect(() => {
		fetchSounds(query);
	}, [query]);

	const fetchSounds = async (searchQuery: string) => {
		try {
			const apiKey = 'Aj9x06vq60VC37YLo9psCPwzvEIyTu0eBQfphtoz';
			const queryParams = new URLSearchParams({
				query: searchQuery,
				fields: 'id,name,previews',
				token: apiKey
			});
			const response = await fetch(
				`https://freesound.org/apiv2/search/text/?${queryParams}`
			);
			const data = await response.json();
			const randomPianoSounds = data.results.slice(0, 16);
			setSounds(randomPianoSounds);
		} catch (error) {
			console.error('Error fetching sounds:', error);
		}
	};

	const handleSoundClick = (previewUrl: string) => {
		const audio = new Audio(previewUrl);
		audio.play();
	};

	const handleSearch = (e: React.FormEvent) => {
		e.preventDefault();
		fetchSounds(query);
	};

	const truncateName = (name: string, maxLength: number) => {
		if (name.length > maxLength) {
			return name.slice(0, maxLength) + '...';
		}
		return name;
	};

	return (
		<div className="main-page">
			<div className="content">
				<div className="logo">
					<h1>SynthPiser</h1>
				</div>
				<form onSubmit={handleSearch}>
					<input
						type="text"
						value={query}
						onChange={(e) => setQuery(e.target.value)}
						placeholder="Search"
						className="search-bar"
					/>
				</form>
				<div className="sounds">
					{sounds.map((sound) => (
						<button
							key={sound.id}
							onClick={() => handleSoundClick(sound.previews['preview-hq-mp3'])}
							className="sound-button"
						>
							{truncateName(sound.name, 10)} {/* Adjust the length as needed */}
						</button>
					))}
				</div>
			</div>
		</div>
	);
};

export default MainPage;
