import React, { useState, useEffect, useRef } from 'react';
import { FaPlay, FaPause, FaRedo } from 'react-icons/fa';

interface BeatControlsProps {
	buttonPins: number[];
}

const BeatControls: React.FC<BeatControlsProps> = ({ buttonPins }) => {
	const [timerValue, setTimerValue] = useState<number>(0);
	const [isPlaying, setIsPlaying] = useState<boolean>(false);
	const [timerRunning, setTimerRunning] = useState<boolean>(false);
	const timerRef = useRef<number | null>(null);

	const togglePlayPause = () => {
		setIsPlaying(!isPlaying);
		if (!timerRunning) {
			setTimerValue(0);
			setTimerRunning(true);
			timerRef.current = window.setInterval(() => {
				setTimerValue((prevValue) => {
					if (prevValue >= 10) {
						setTimerRunning(false);
						window.clearInterval(timerRef.current!);
						return 0;
					}
					return prevValue + 1;
				});
			}, 1000);
		} else {
			setTimerRunning(false);
			window.clearInterval(timerRef.current!);
		}
	};

	const resetAudio = () => {
		setTimerValue(0);
		setIsPlaying(false);
		setTimerRunning(false);
		if (timerRef.current) {
			clearInterval(timerRef.current);
		}
	};

	const handleResetSounds = () => {
		// Reset assigned sounds or any other state related to sounds
	};

	const playPauseButton = timerValue > 10 ? <FaPlay /> : <FaPause />;

	return (
		<div className="beat-controls">
			<div className="visualizer">
				<div className="timer-wrapper">
					<div className="timer">{timerValue}</div>
				</div>
				<div className="timer-line" style={{ width: `${timerValue * 10}%` }} />
			</div>
			<div className="beat-buttons">
				<button onClick={togglePlayPause}>{playPauseButton}</button>
				<button onClick={resetAudio}>
					<FaRedo />
				</button>
				<button className="reset-button" onClick={handleResetSounds}>
					Reset
				</button>
			</div>
		</div>
	);
};

export default BeatControls;
