import React, { useState, useEffect } from 'react';

interface Sound {
	id: number;
	name: string;
	previews: {
		'preview-hq-mp3': string;
	};
	pack: string;
}

interface Pack {
	id: number;
	name: string;
}

const MainPage = () => {
	const [sounds, setSounds] = useState<Sound[]>([]);
	const [packs, setPacks] = useState<Pack[]>([]);
	const [selectedPackId, setSelectedPackId] = useState<number | null>(null);

	useEffect(() => {
		const fetchPacks = async () => {
			try {
				const apiKey = 'Aj9x06vq60VC37YLo9psCPwzvEIyTu0eBQfphtoz';
				const response = await fetch(
					`https://freesound.org/apiv2/packs/?fields=id,name&token=${apiKey}&page_size=10`
				);
				const data = await response.json();
				setPacks(data.results);
				console.log('Fetched packs:', data.results); // Log fetched packs
			} catch (error) {
				console.error('Error fetching packs:', error);
			}
		};

		fetchPacks();
	}, []);

	useEffect(() => {
		console.log('Selected pack ID:', selectedPackId); // Log selected pack ID
		if (selectedPackId !== null) {
			const fetchSoundsFromPack = async () => {
				try {
					const apiKey = 'Aj9x06vq60VC37YLo9psCPwzvEIyTu0eBQfphtoz';
					const queryParams = new URLSearchParams({
						fields: 'id,name,previews,pack',
						token: apiKey
					});
					const response = await fetch(
						`https://freesound.org/apiv2/search/text/?${queryParams}&pack=${selectedPackId}`
					);
					const data = await response.json();
					const soundsFromPack = data.results.slice(0, 16);
					setSounds(soundsFromPack);
					console.log('Fetched sounds from pack:', soundsFromPack); // Log fetched sounds
				} catch (error) {
					console.error('Error fetching sounds from pack:', error);
				}
			};

			fetchSoundsFromPack();
		}
	}, [selectedPackId]);

	const handleSoundClick = (previewUrl: string) => {
		const audio = new Audio(previewUrl);
		audio.play();
	};

	const handlePackClick = (packName: string) => {
		const selectedPack = packs.find((pack) => pack.name === packName);
		if (selectedPack) {
			setSelectedPackId(selectedPack.id);
		}
	};

	return (
		<div className="main-page">
			<div className="content">
				<div className="logo">
					<h1>SynthPiser</h1>
				</div>
				<div className="packs">
					{packs.map((pack) => (
						<button
							key={pack.id}
							onClick={() => handlePackClick(pack.name)}
							className="pack-button"
						>
							{pack.name}
						</button>
					))}
				</div>
				<div className="sounds">
					{sounds.map((sound) => (
						<button
							key={sound.id}
							onClick={() => handleSoundClick(sound.previews['preview-hq-mp3'])}
							className="sound-button"
						>
							{sound.name} <br />
							<span className="packle">{sound.pack}</span>
						</button>
					))}
				</div>
			</div>
		</div>
	);
};

export default MainPage;
