import React, { useState, useEffect } from 'react';

const MainPage: React.FC = () => {
	const [selectedPack, setSelectedPack] = useState<string | null>(null);
	const [sounds, setSounds] = useState<string[]>([]);

	useEffect(() => {
		if (selectedPack) {
			fetchSounds(selectedPack);
		}
	}, [selectedPack]);

	const fetchSounds = async (pack: string) => {
		try {
			const response = await fetch(
				`https://freesound.org/apiv2/search/text/?query=${pack}&token=YOUR_API_KEY`
			);
			if (!response.ok) {
				throw new Error(`Failed to fetch sounds for pack ${pack}`);
			}
			const data = await response.json();
			setSounds(data.results.map((result: any) => result.url));
		} catch (error) {
			console.error('Error fetching sounds:', error);
		}
	};

	const handlePackClick = (pack: string) => {
		setSelectedPack(pack);
	};

	const handleSoundDragStart =
		(soundUrl: string) => (event: React.DragEvent<HTMLButtonElement>) => {
			event.dataTransfer.setData('text/plain', soundUrl);
		};

	const handleButtonDrop = (event: React.DragEvent<HTMLButtonElement>) => {
		const soundUrl = event.dataTransfer.getData('text/plain');
		// Handle drop event, e.g., assign soundUrl to the button
	};

	const renderPacks = () => {
		const packs = ['drums', 'piano' /* Add more packs here */];
		return packs.map((pack, index) => (
			<button key={index} onClick={() => handlePackClick(pack)}>
				{pack}
			</button>
		));
	};

	const renderSounds = () => {
		return sounds.map((soundUrl, index) => (
			<button
				key={index}
				draggable
				onDragStart={handleSoundDragStart(soundUrl)}
			>
				Sound {index + 1}
			</button>
		));
	};

	const renderButtons = () => {
		return Array.from({ length: 16 }, (_, index) => (
			<button
				key={index}
				onDrop={handleButtonDrop}
				onDragOver={(event) => event.preventDefault()}
			>
				{index + 1}
			</button>
		));
	};

	return (
		<div className="main-page">
			<div className="logo">
				<h1>SynthPiser</h1>
			</div>
			<div className="packs">{renderPacks()}</div>
			<div className="sounds">{renderSounds()}</div>
			<div className="buttons">{renderButtons()}</div>
		</div>
	);
};

export default MainPage;
