import React, { useState, useEffect } from 'react';

interface Sound {
	id: number;
	name: string;
	previews: {
		'preview-hq-mp3': string;
	};
}

const MainPage = () => {
	const [sounds, setSounds] = useState<Sound[]>([]);

	useEffect(() => {
		const fetchRandomSounds = async () => {
			try {
				const apiKey = 'Aj9x06vq60VC37YLo9psCPwzvEIyTu0eBQfphtoz';
				const randomIds = Array.from({ length: 16 }, () =>
					Math.floor(Math.random() * 500000)
				);
				const promises = randomIds.map((id) =>
					fetch(
						`https://freesound.org/apiv2/sounds/${id}/?token=${apiKey}`
					).then((response) => response.json())
				);
				const randomSounds = await Promise.all(promises);

				// Shorten the names of the sounds
				const shortenedSounds = randomSounds.map((sound) => ({
					...sound,
					name: shortenName(sound.name)
				}));

				setSounds(shortenedSounds);
			} catch (error) {
				console.error('Error fetching random sounds:', error);
			}
		};

		fetchRandomSounds();
	}, []);

	const handleSoundClick = (previewUrl: string) => {
		const audio = new Audio(previewUrl);
		audio.play();
	};

	const shortenName = (name: string) => {
		const maxLength = 15;
		return name.length > maxLength
			? name.substring(0, maxLength) + '...'
			: name;
	};

	return (
		<div className="main-page">
			<div className="content">
				<div className="logo">
					<h1>SynthPiser</h1>
				</div>
				<div className="sounds">
					{sounds.map((sound) => (
						<button
							key={sound.id}
							onClick={() => handleSoundClick(sound.previews['preview-hq-mp3'])}
							className="sound-button"
						>
							{sound.name}
						</button>
					))}
				</div>
			</div>
		</div>
	);
};

export default MainPage;
