import React, { useState, useEffect } from 'react';

const MainPage: React.FC = () => {
	const [sounds, setSounds] = useState<string[]>([]);

	useEffect(() => {
		fetchSounds();
	}, []);

	const fetchSounds = async () => {
		try {
			const response = await fetch(
				`https://freesound.org/apiv2/search/text/?query=piano&page=2`
			);
			if (!response.ok) {
				throw new Error('Failed to fetch piano sounds');
			}
			const data = await response.json();
			setSounds(data.results.map((result: any) => result.url));
		} catch (error) {
			console.error('Error fetching piano sounds:', error);
		}
	};

	const handleSoundClick = (soundUrl: string) => {
		const audio = new Audio(soundUrl);
		audio.play().catch((error) => {
			console.error('Error playing sound:', error);
		});
	};

	const renderSounds = () => {
		return sounds.map((soundUrl, index) => (
			<button key={index} onClick={() => handleSoundClick(soundUrl)}>
				Sound {index + 1}
			</button>
		));
	};

	return (
		<div className="main-page">
			<div className="logo">
				<h1>SynthPiser</h1>
			</div>
			<div className="sounds">{renderSounds()}</div>
		</div>
	);
};

export default MainPage;
