import React, { useState, useEffect } from 'react';
import { Sound } from '../pages/MainPage';

interface Preset {
  name: string;
  sounds: Sound[];
}

interface PresetsDropdownProps {
  handlePresetSelect: (sounds: Sound[]) => void;
  socket: WebSocket | null; // Add socket prop
  buttonPins: number[]; // Add buttonPins prop
}

const PresetsDropdown: React.FC<PresetsDropdownProps> = ({ handlePresetSelect, socket, buttonPins }) => {
  const [isOpen, setIsOpen] = useState(false);
  const [selectedPreset, setSelectedPreset] = useState<string>('Presets');
  const [pianoSounds, setPianoSounds] = useState<Sound[]>([]);
  
  useEffect(() => {
    const fetchPianoSounds = async () => {
      try {
        const apiKey = 'Aj9x06vq60VC37YLo9psCPwzvEIyTu0eBQfphtoz';
        const notes = ['C', 'D', 'E', 'F', 'G', 'A', 'B']; // Desired piano notes
        const soundPromises = notes.map(async (note) => {
          const queryParams = new URLSearchParams({
            query: `piano ${note}`,
            fields: 'id,name,bitrate,images,previews,tags',
            token: apiKey
          });
          const response = await fetch(`https://freesound.org/apiv2/search/text/?${queryParams}`);
          const data = await response.json();
          return data.results[0]; // Take only the first result for each note
        });
        const resolvedSounds = await Promise.all(soundPromises);
        setPianoSounds(resolvedSounds.filter(sound => sound !== undefined)); // Filter out undefined values
      } catch (error) {
        console.error('Error fetching piano sounds:', error);
      }
    };
    
    fetchPianoSounds();
  }, []);

  const presets: Preset[] = [
    { name: 'Piano Notes', sounds: pianoSounds },
    { name: 'Guitar Chords', sounds: [] }, // Add your sounds here
    // Add more presets as needed
  ];

  const handlePresetClick = (preset: Preset) => {
    preset.sounds.forEach((sound, index) => {
      const pin = buttonPins[index];
      console.log('Sending sound to backend:', { pin, sound });
      if (socket) {
        socket.send(JSON.stringify({ type: 'assign_sound', pin, sound }));
      }
    });
    handlePresetSelect(preset.sounds);
    setSelectedPreset(preset.name);
    setIsOpen(false);
  };

  return (
    <div className="presets">
      <div className="presets-dropdown">
        <button className="preset-button" onClick={() => setIsOpen(!isOpen)}>
          {selectedPreset}
        </button>
        {isOpen && (
          <div className="dropdown-menu">
            {presets.map((preset, index) => (
              <button
                className='preset-button-dropdown'
                key={index}
                onClick={() => handlePresetClick(preset)}
              >
                {preset.name}
              </button>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default PresetsDropdown;
