import React, { useState, useEffect, useRef } from 'react';
import { FaPlay, FaPause, FaStop, FaRedo, FaSquare, FaReply } from 'react-icons/fa';

type Sound = {
  id: number;
  preview: string;
};

type BeatControlsProps = {
  socket: WebSocket | null;
  assignedSounds: Sound[];
};

const BeatControls = ({ socket, assignedSounds }: BeatControlsProps) => {
  const [timerValue, setTimerValue] = useState<number>(0);
  const [isPlaying, setIsPlaying] = useState<boolean>(false);
  const [recordedButtons, setRecordedButtons] = useState<{ time: number; pin: number }[]>([]);
  const [axisPosition, setAxisPosition] = useState<number>(0);

  const timerRef = useRef<number | null>(null);
  const audioRefs = useRef<(HTMLAudioElement | null)[]>(assignedSounds.map(() => null));

  const togglePlayPause = () => {
    console.log(`Toggle Play/Pause: ${!isPlaying ? 'Play' : 'Pause'}`);
    if (isPlaying) {
      pauseTimer();
    } else {
      startTimer();
    }
  };

  const startTimer = () => {
    console.log('Starting timer...');
    setIsPlaying(true);
    timerRef.current = window.setInterval(() => {
      setTimerValue((prevValue) => prevValue + 100);
      playSoundsAtCurrentTime();
      console.log(`Timer value: ${timerValue}`);
    }, 100);
  };

  const pauseTimer = () => {
    console.log('Pausing timer...');
    setIsPlaying(false);
    if (timerRef.current !== null) {
      clearInterval(timerRef.current);
    }
  };

  const stopTimer = () => {
    console.log('Stopping timer...');
    pauseTimer();
    setTimerValue(0);
    setRecordedButtons([]);
  };

  const replayRecordedButtons = () => {
    console.log('Replaying recorded buttons...');
    stopTimer();
    startTimer();
  };

  const playSoundsAtCurrentTime = () => {
    recordedButtons.forEach((button) => {
      if (button.time === timerValue) {
        console.log(`Playing sound for recorded button at time: ${button.time} ms`);
        const audio = audioRefs.current[button.pin];
        if (audio) {
          audio.currentTime = 0;
          audio.play();
        }
      }
    });
  };

  const handleButtonClick = (pin: number) => {
    console.log(`Button click received from pin ${pin}`);
    if (!isPlaying) {
      const time = timerValue;
      console.log(`Button pressed at time: ${time} ms`);
      setRecordedButtons((prevButtons) => [...prevButtons, { time, pin }]);
    }
  };

  useEffect(() => {
    if (socket) {
      socket.onmessage = (event: MessageEvent) => {
        const message = JSON.parse(event.data);
        if (message.type === 'button_click') {
          handleButtonClick(message.pin);
        } else if (message.type === 'assigned_sounds') {
          console.log('Received assigned sounds:', message.sounds);
          // Handle assigned sounds
        }
      };
    }
    // Clean up
    return () => {
      if (socket) {
        socket.onmessage = null;
      }
    };
  }, [socket]);

  return (
    <div className="beat-controls">
      <div className="timer-display">
        <span>{formatMilliseconds(timerValue)}</span>
      </div>
      <div className="timer-axis">
        {recordedButtons.map((button, index) => (
          <div
            key={index}
            className="button-indicator"
            style={{ left: `${(button.time / 10000) * 100}%` }}
          ></div>
        ))}
        <div
          className="timer-axis-display"
          style={{ left: `${(axisPosition / 10000) * 100}%` }}
        ></div>
      </div>
      <div className="playback-controls">
        <button onClick={togglePlayPause}>
          {isPlaying ? <FaPause /> : <FaPlay />} {isPlaying ? 'Pause' : 'Play'}
        </button>
        <button onClick={stopPlayback}>
          <FaStop /> Stop
        </button>
        <button onClick={resetTimer}>
          <FaRedo /> Reset
        </button>
        <button onClick={handleClearRecordedButtons}>
          <FaSquare /> Clear Recorded
        </button>
      </div>
      {Object.keys(assignedSounds).map((pin, index) => (
        <audio
          key={pin}
          ref={(el) => (audioRefs.current[index] = el)}
          src={assignedSounds[parseInt(pin, 10)] || undefined}
        ></audio>
      ))}
    </div>
  );
};

export default BeatControls;
