import React, { useState, useEffect } from 'react';

interface Sound {
	id: number;
	name: string;
	preview_url: string;
}

const MainPage = () => {
	const [sounds, setSounds] = useState<Sound[]>([]);

	useEffect(() => {
		const fetchSounds = async () => {
			try {
				const apiKey = 'Aj9x06vq60VC37YLo9psCPwzvEIyTu0eBQfphtoz';
				const response = await fetch(
					'https://freesound.org/apiv2/search/text/?query=piano&token=' + apiKey
				);
				const data = await response.json();
				setSounds(data.results.slice(0, 16));
			} catch (error) {
				console.error('Error fetching sounds:', error);
			}
		};

		fetchSounds();
	}, []);

	const handleSoundClick = async (sound: Sound) => {
		try {
			const response = await fetch(
				`https://freesound.org/apiv2/sounds/${sound.id}/`
			);
			const soundData = await response.json();

			// Check if the soundData contains previews
			if (soundData.previews) {
				// Use the preview-hq-mp3 URL if available
				const previewHQMP3 = soundData.previews['preview-hq-mp3'];
				if (previewHQMP3) {
					const audio = new Audio(previewHQMP3);
					audio.play();
				} else {
					console.error('No preview-hq-mp3 URL available for the sound.');
				}
			} else {
				console.error('No previews available for the sound.');
			}
		} catch (error) {
			console.error('Error fetching sound data:', error);
		}
	};

	return (
		<div className="main-page">
			<div className="content">
				<div className="logo">
					<h1>SynthPiser</h1>
				</div>
				<div className="sounds">
					{sounds.map((sound) => (
						<button
							key={sound.id}
							onClick={() => handleSoundClick(sound)}
							className="sound-button"
						>
							{sound.name}
						</button>
					))}
				</div>
			</div>
		</div>
	);
};

export default MainPage;
